$(document).ready(function(){$("#email-form").validate({rules:{name:{required:!0,minlength:4},subject:{required:!0,minlength:6},email:{required:!0,email:!0},message:{required:!0},captcha:{required:!0}},messages:{name:"Please enter your name",subject:"Please enter your subject",message:"Please enter your message",email:"Please enter a valid email address",captcha:"Please verify captcha"},submitHandler:function(e){let a=$(e);$.ajax({type:"POST",url:"https://profile-app-test.fly.dev/api/portfolio/contact",data:a.serialize(),beforeSend:function(){$("#loading").show(),$("#submit-form").hide()},success:function(e){$("#message-modal").html("Your message has been sent<br/>I will reply you soon")},error:function(e){$("#message-modal").html("Have problems with your message<br/>Please try again later<br/>Or contact me via email, telegram, linkedin, etc...")},complete:function(e){$("#loading").hide(),$("#submit-form").show(),$("#popup-modal").modal("show")}})}})});